import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./ManageRecipeMealList.css"

function ManageRecipeMealList() {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        const fetchRecipes = async () => {
            try {
                const response = await axios.get('http://localhost:5000/recipe-list');
                setRecipes(response.data);
            } catch (error) {
                console.error('Error loading recipes:', error);
            }
        };

        fetchRecipes();
    }, []);

    const handleDeleteRecipe = async (recipeId) => {
        const confirmDelete = window.confirm('Are you sure you want to delete this recipe?');
        if (confirmDelete) {
            try {
                await axios.delete(`http://localhost:5000/recipe-list/${recipeId}`);
                const updatedRecipes = recipes.filter(recipe => recipe._id !== recipeId);
                setRecipes(updatedRecipes);
                console.log('Recipe deleted successfully.');
            } catch (error) {
                console.error('Error deleting recipe:', error);
            }
        }
    };

    return (
        <div className="mr-body">
            <div className="mr-background"></div>
            <h2 className="mr-title">List of Recipes</h2>
            <div className="mr-section">
            <ul>
                <div className="mr-list">
                {recipes.map(recipe => (
                    <li key={recipe._id} className="mr-list">
                        <div>
                            <h3 className="mr-list-title">{recipe.title}</h3>
                            <p className="mr-list-time">Preparation Time: {recipe.prepTime} minutes</p>
                            <p className="mr-list-time">Cooking Time: {recipe.cookTime} minutes</p>
                            <button className="mr-remove-btn" onClick={() => handleDeleteRecipe(recipe._id)}>Delete</button>
                        </div>
                    </li>
                ))}
            </div>
            </ul>
            </div>
        </div>
    );
}

export default ManageRecipeMealList;
