import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Calendar as BigCalendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

const Calendar = () => {
    const [events, setEvents] = useState([]);
    const [recipes, setRecipes] = useState([]);
    const [shoppingLists, setShoppingLists] = useState([]);
    const [selectedEvent, setSelectedEvent] = useState(null);
    const [selectedDate, setSelectedDate] = useState(null);
    const [recurringOption, setRecurringOption] = useState('unique');

    useEffect(() => {
        fetchRecipes();
        fetchShoppingLists();
    }, []);

    const fetchRecipes = async () => {
        try {
            const response = await axios.get('http://localhost:5000/recipe-list');
            setRecipes(response.data);
        } catch (error) {
            console.error('Erreur lors de la récupération des recettes :', error);
        }
    };

    const fetchShoppingLists = async () => {
        try {
            const response = await axios.get('http://localhost:5000/shopping-list');
            setShoppingLists(response.data);
        } catch (error) {
            console.error('Erreur lors de la récupération des listes de courses :', error);
        }
    };

    const handleEventSelect = (event) => {
        setSelectedEvent(event);
        setSelectedDate(null);
    };

    const handleDateSelect = (slotInfo) => {
        setSelectedDate(slotInfo.start);
    };

    const handleAddToCalendar = (item) => {
        setSelectedEvent(item);
        setSelectedDate(null); // Réinitialiser la date sélectionnée
    };

    const handleRecurringOptionChange = (event) => {
        setRecurringOption(event.target.value);
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();
        const start = selectedDate;
        const end = selectedDate;
        const newEvent = {
            title: selectedEvent.title,
            start,
            end,
            recurringOption,
        };
        console.log('Nouvel événement:', newEvent);
        // Envoyer les données du formulaire pour sauvegarde dans la base de données
        // Réinitialiser l'état après la soumission du formulaire
        setSelectedEvent(null);
        setSelectedDate(null);
        setRecurringOption('unique');
    };

    return (
        <div>
            <h2>Calendrier</h2>
            <div style={{ height: '500px' }}>
                <BigCalendar
                    localizer={localizer}
                    events={events}
                    selectable
                    onSelectEvent={handleEventSelect}
                    onSelectSlot={handleDateSelect}
                />
            </div>
            {selectedEvent && (
                <form onSubmit={handleFormSubmit}>
                    <h3>Ajouter {selectedEvent.title} au calendrier</h3>
                    {selectedDate && (
                        <p>Date sélectionnée: {moment(selectedDate).format('YYYY-MM-DD')}</p>
                    )}
                    <label>
                        Options de récurrence :
                        <select value={recurringOption} onChange={handleRecurringOptionChange}>
                            <option value="unique">Unique</option>
                            <option value="daily">Quotidienne</option>
                            <option value="weekly">Hebdomadaire</option>
                            <option value="monthly">Mensuelle</option>
                        </select>
                    </label>
                    <button type="submit">Ajouter</button>
                </form>
            )}
            <h3>Recettes</h3>
            <ul>
                {recipes.map((recipe) => (
                    <li key={recipe._id}>
                        <div>
                            <h4>{recipe.title}</h4>
                            <button onClick={() => handleAddToCalendar(recipe)}>
                                Ajouter au calendrier
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
            <h3>Listes de courses</h3>
            <ul>
                {shoppingLists.map((list) => (
                    <li key={list._id}>
                        <div>
                            <h4>{list.listTitle}</h4>
                            <button onClick={() => handleAddToCalendar(list)}>
                                Ajouter au calendrier
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Calendar;
