import React, { useState } from 'react';

function ListeRecettesPage() {
    // Utilisation du state local pour gérer les recettes
    const [nouvelleRecette, setNouvelleRecette] = useState('');
    const [listeRecettes, setListeRecettes] = useState([]);

    // Fonction pour ajouter une nouvelle recette à la liste
    const ajouterRecette = () => {
        if (nouvelleRecette.trim() !== '') {
            setListeRecettes([...listeRecettes, nouvelleRecette]);
            setNouvelleRecette('');
        }
    };

    // Fonction pour supprimer une recette de la liste
    const supprimerRecette = (index) => {
        const nouvelleListe = [...listeRecettes];
        nouvelleListe.splice(index, 1);
        setListeRecettes(nouvelleListe);
    };

    return (
        <div>
            <h2>Liste de recettes de plats</h2>
            <div>
                {/* Formulaire pour ajouter une nouvelle recette */}
                <input
                    type="text"
                    value={nouvelleRecette}
                    onChange={(e) => setNouvelleRecette(e.target.value)}
                    placeholder="Ajouter une nouvelle recette"
                />
                <button onClick={ajouterRecette}>Ajouter</button>
            </div>
            {/* Affichage de la liste de recettes */}
            <ul>
                {listeRecettes.map((recette, index) => (
                    <li key={index}>
                        {recette}
                        <button onClick={() => supprimerRecette(index)}>Supprimer</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ListeRecettesPage;
