import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDrumstickBite, faFish, faAppleAlt, faCarrot, faCheese, faShoppingBag, faLeaf, faSnowflake, faBoxes, faWineBottle, faCandyCane } from '@fortawesome/free-solid-svg-icons';
import "./ManageShoppingFoodList.css";

const ManageShoppingFoodList = () => {
    const [shoppingLists, setShoppingLists] = useState([]);
    const [expandedLists, setExpandedLists] = useState([]);

    useEffect(() => {
        fetchShoppingLists();
    }, []);

    const fetchShoppingLists = async () => {
        try {
            const response = await axios.get('http://localhost:5000/shopping-list');
            setShoppingLists(response.data);
            setExpandedLists(new Array(response.data.length).fill(false));
        } catch (error) {
            console.error('Error fetching shopping lists:', error);
        }
    };

    const handleDeleteList = async (listId) => {
        try {
            await axios.delete(`http://localhost:5000/shopping-list/${listId}`);
            fetchShoppingLists();
        } catch (error) {
            console.error('Error deleting shopping list:', error);
        }
    };

    const toggleExpandList = (index) => {
        const updatedExpandedLists = [...expandedLists];
        updatedExpandedLists[index] = !updatedExpandedLists[index];
        setExpandedLists(updatedExpandedLists);
    };

    const getCategoryIcon = (category) => {
        switch (category) {
            case 'Meat':
                return faDrumstickBite;
            case 'Fish and Seafood':
                return faFish;
            case 'Fruits':
                return faAppleAlt;
            case 'Vegetables':
                return faCarrot;
            case 'Dairy Products':
                return faCheese;
            case 'Dry Goods':
                return faShoppingBag;
            case 'Fresh Products':
                return faLeaf;
            case 'Frozen Foods':
                return faSnowflake;
            case 'Grocery':
                return faBoxes;
            case 'Beverages':
                return faWineBottle;
            case 'Snacks':
                return faCandyCane;
            default:
                return null;
        }
    };

    return (
        <div className="ms-body">
            <div className="ms-background"></div>
            <h1 className="ms-title">Shopping Lists</h1>
            <div className="ms-section">
                <ul className="ms-lists">
                    {shoppingLists.map((list, index) => (
                        <li key={list._id} className="ms-list">
                            <h3 className="ms-list-title">{list.listTitle}</h3>
                            <ul className="ms-items">
                                {list.items.slice(0, expandedLists[index] ? list.items.length : 4).map((item, itemIndex) => (
                                    <li key={`${list._id}-${itemIndex}`} className="ms-item">
                                        <div className="ms-item-info">
                                            <div className="ms-item-icon">
                                                {getCategoryIcon(item.category) &&
                                                    <FontAwesomeIcon icon={getCategoryIcon(item.category)}/>}
                                            </div>
                                            <div className="ms-item-details">
                                                <p className="ms-item-name">{item.name}</p>
                                                <p className="ms-item-quantity">Quantity: {item.quantity}</p>
                                            </div>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            {list.items.length > 4 && (
                                <div className="frame">
                                    <button className="custom-btn ms-show-more-btn"
                                            onClick={() => toggleExpandList(index)}>
                                        {expandedLists[index] ? 'Less' : 'More'}
                                    </button>
                                </div>
                            )}
                            <div className="frame">
                                <button className="custom-btn ms-remove-btn"
                                        onClick={() => handleDeleteList(list._id)}>Delete
                                </button>
                            </div>
                        </li>
                        ))}
                </ul>
            </div>
        </div>
    );
};

export default ManageShoppingFoodList;
