import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Calendar = () => {
    const [events, setEvents] = useState([]);
    const [newEvent, setNewEvent] = useState('');
    const [recipes, setRecipes] = useState([]);
    const [shoppingLists, setShoppingLists] = useState([]);

    useEffect(() => {
        fetchRecipes();
        fetchShoppingLists();
    }, []);

    const fetchRecipes = async () => {
        try {
            const response = await axios.get('http://localhost:5000/recipe-list');
            setRecipes(response.data);
        } catch (error) {
            console.error('Erreur lors de la récupération des recettes :', error);
        }
    };

    const fetchShoppingLists = async () => {
        try {
            const response = await axios.get('http://localhost:5000/shopping-list');
            setShoppingLists(response.data);
        } catch (error) {
            console.error('Erreur lors de la récupération des listes de courses :', error);
        }
    };

    const addEvent = (event) => {
        if (event.trim() !== '') {
            setEvents([...events, event]);
            setNewEvent('');
        }
    };

    const removeEvent = (index) => {
        const updatedEvents = [...events];
        updatedEvents.splice(index, 1);
        setEvents(updatedEvents);
    };

    return (
        <div>
            <h2>Calendar</h2>
            <div>
                <input
                    type="text"
                    value={newEvent}
                    onChange={(e) => setNewEvent(e.target.value)}
                    placeholder="Enter event name"
                />
                <button onClick={() => addEvent(newEvent)}>Add Event</button>
            </div>
            <h3>Recipes</h3>
            <ul>
                {recipes.map((recipe) => (
                    <li key={recipe._id}>
                        <button onClick={() => addEvent(recipe.title)}>
                            {recipe.title}
                        </button>
                    </li>
                ))}
            </ul>
            <h3>Shopping Lists</h3>
            <ul>
                {shoppingLists.map((list) => (
                    <li key={list._id}>
                        <button onClick={() => addEvent(list.listTitle)}>
                            {list.listTitle}
                        </button>
                    </li>
                ))}
            </ul>
            <h3>Events</h3>
            <ul>
                {events.map((event, index) => (
                    <li key={index}>
                        {event}{' '}
                        <button onClick={() => removeEvent(index)}>Remove</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Calendar;
