import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ManageRecipeMealList = () => {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        // Récupérer les recettes depuis le serveur lors du chargement initial
        const fetchRecipes = async () => {
            try {
                const response = await axios.get('http://localhost:5000/recipe-list');
                setRecipes(response.data);
            } catch (error) {
                console.error('Erreur lors du chargement des recettes :', error);
            }
        };

        fetchRecipes();
    }, []);

    const handleDelete = async (recipeId) => {
        try {
            await axios.delete(`http://localhost:5000/recipe-list/${recipeId}`);
            // Actualiser l'interface utilisateur après la suppression réussie
            setRecipes(recipes.filter(recipe => recipe._id !== recipeId));
        } catch (error) {
            console.error('Erreur lors de la suppression de la recette :', error);
            // Gérer les erreurs de suppression
        }
    };

    return (
        <div>
            <h2>Liste des recettes de cuisine</h2>
            {recipes.map(recipe => (
                <div key={recipe._id} className="recipe-card">
                    <h3>{recipe.title}</h3>
                    <p>Ingrédients: {recipe.ingredients.join(', ')}</p>
                    <p>Instructions: {recipe.instructions}</p>
                    <button onClick={() => handleDelete(recipe._id)}>Supprimer</button>
                    {/* Bouton Modifier pourrait rediriger vers la page de modification de la recette */}
                </div>
            ))}
        </div>
    );
};

export default ManageRecipeMealList;
