import React, { useState } from 'react';

function RecipeForm() {
    const [recipe, setRecipe] = useState({
        title: '',
        ingredients: '',
        instructions: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecipe(prevRecipe => ({
            ...prevRecipe,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Vous pouvez ici ajouter du code pour traiter les données de la recette, par exemple, les enregistrer dans une base de données
        console.log('Nouvelle recette:', recipe);
        // Réinitialisation du formulaire après la soumission
        setRecipe({
            title: '',
            ingredients: '',
            instructions: ''
        });
    };

    return (
        <div>
            <h2>Créer une nouvelle recette</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="title">Titre de la recette:</label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={recipe.title}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="ingredients">Ingrédients:</label>
                    <textarea
                        id="ingredients"
                        name="ingredients"
                        value={recipe.ingredients}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="instructions">Instructions:</label>
                    <textarea
                        id="instructions"
                        name="instructions"
                        value={recipe.instructions}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">Ajouter la recette</button>
            </form>
        </div>
    );
}

export default RecipeForm;
