import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDrumstickBite, faFish, faAppleAlt, faCarrot, faCheese, faShoppingBag, faLeaf, faSnowflake, faBoxes, faWineBottle, faCandyCane } from '@fortawesome/free-solid-svg-icons';
import "./ManageShoppingFoodList.css";

const ManageShoppingFoodList = () => {
    const [shoppingLists, setShoppingLists] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        fetchShoppingLists();
    }, []);

    const fetchShoppingLists = async () => {
        try {
            const response = await axios.get('http://localhost:5000/shopping-list');
            setShoppingLists(response.data);
        } catch (error) {
            console.error('Error fetching shopping lists:', error);
        }
    };

    const handleDeleteList = async (listId) => {
        try {
            await axios.delete(`http://localhost:5000/shopping-list/${listId}`);
            fetchShoppingLists();
        } catch (error) {
            console.error('Error deleting shopping list:', error);
        }
    };

    const getCategoryIcon = (category) => {
        switch (category) {
            case 'Meat':
                return faDrumstickBite;
            case 'Fish and Seafood':
                return faFish;
            case 'Fruits':
                return faAppleAlt;
            case 'Vegetables':
                return faCarrot;
            case 'Dairy Products':
                return faCheese;
            case 'Dry Goods':
                return faShoppingBag;
            case 'Fresh Products':
                return faLeaf;
            case 'Frozen Foods':
                return faSnowflake;
            case 'Grocery':
                return faBoxes;
            case 'Beverages':
                return faWineBottle;
            case 'Snacks':
                return faCandyCane;
            default:
                return null;
        }
    };

    const handleNextList = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % shoppingLists.length);
    };

    const handlePreviousList = () => {
        setCurrentIndex((prevIndex) => (prevIndex - 1 + shoppingLists.length) % shoppingLists.length);
    };

    return (
        <div className="ms-body">
            <div className="ms-background"></div>
            <h1 className="ms-title">Shopping Lists</h1>
            {shoppingLists.length > 0 && (
                <div className="ms-section">
                    <ul className="ms-lists">
                        <li className="ms-list">
                            <h3 className="ms-list-title">{shoppingLists[currentIndex].listTitle}</h3>
                            <ul className="ms-items">
                                {shoppingLists[currentIndex].items.slice(0, 4).map((item, itemIndex) => (
                                    <li key={`${item._id}`} className="ms-item">
                                        <div className="ms-item-info">
                                            <div className="ms-item-icon">
                                                {getCategoryIcon(item.category) && (
                                                    <FontAwesomeIcon icon={getCategoryIcon(item.category)} />
                                                )}
                                            </div>
                                            <div className="ms-item-details">
                                                <p className="ms-item-name">{item.name}</p>
                                                <p className="ms-item-quantity">Quantity: {item.quantity}</p>
                                            </div>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <div className="frame">
                                <button className="custom-btn ms-remove-btn" onClick={() => handleDeleteList(shoppingLists[currentIndex]._id)}>
                                    Delete
                                </button>
                            </div>
                        </li>
                    </ul>
                    {shoppingLists.length > 1 && (
                        <div className="frame">
                            <button className="custom-btn ms-navigation-btn" onClick={handlePreviousList}>
                                Previous
                            </button>
                            <button className="custom-btn ms-navigation-btn" onClick={handleNextList}>
                                Next
                            </button>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default ManageShoppingFoodList;
