import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment);

function CalendrierPage() {
    const [evenements, setEvenements] = useState([]);

    // Fonction pour gérer l'ajout d'un événement au calendrier
    const handleAjouterEvenement = (evenement) => {
        setEvenements([...evenements, evenement]);
    };

    return (
        <div>
            <h2>Calendrier</h2>
            {/* Calendrier */}
            <div style={{ height: 500 }}>
                <Calendar
                    localizer={localizer}
                    events={evenements}
                    startAccessor="start"
                    endAccessor="end"
                    style={{ margin: '20px' }}
                />
            </div>
            {/* Formulaire pour ajouter un événement */}
            <div>
                <h3>Ajouter un événement au calendrier :</h3>
                <form onSubmit={(e) => {
                    e.preventDefault();
                    const titre = e.target.titre.value;
                    const debut = new Date(e.target.debut.value);
                    const fin = new Date(e.target.fin.value);
                    handleAjouterEvenement({ title: titre, start: debut, end: fin });
                    e.target.reset();
                }}>
                    <div>
                        <label htmlFor="titre">Titre :</label>
                        <input type="text" id="titre" required />
                    </div>
                    <div>
                        <label htmlFor="debut">Début :</label>
                        <input type="datetime-local" id="debut" required />
                    </div>
                    <div>
                        <label htmlFor="fin">Fin :</label>
                        <input type="datetime-local" id="fin" required />
                    </div>
                    <button type="submit">Ajouter</button>
                </form>
            </div>
        </div>
    );
}

export default CalendrierPage;
