import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDrumstickBite, faFish, faAppleAlt, faCarrot, faCheese, faShoppingBag, faLeaf, faSnowflake, faBoxes, faWineBottle, faCandyCane } from '@fortawesome/free-solid-svg-icons';
import "./ManageShoppingFoodList.css";

const ManageShoppingFoodList = () => {
    const [shoppingLists, setShoppingLists] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        fetchShoppingLists();
    }, []);

    const fetchShoppingLists = async () => {
        try {
            const response = await axios.get('http://localhost:5000/shopping-list');
            setShoppingLists(response.data);
        } catch (error) {
            console.error('Error fetching shopping lists:', error);
        }
    };

    const handleDeleteList = async (listId) => {
        try {
            await axios.delete(`http://localhost:5000/shopping-list/${listId}`);
            fetchShoppingLists();
        } catch (error) {
            console.error('Error deleting shopping list:', error);
        }
    };

    const handlePrevious = () => {
        setCurrentIndex((prevIndex) => (prevIndex === 0 ? shoppingLists.length - 1 : prevIndex - 1));
    };

    const handleNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex === shoppingLists.length - 1 ? 0 : prevIndex + 1));
    };

    const getCategoryIcon = (category) => {
        switch (category) {
            case 'Meat':
                return faDrumstickBite;
            case 'Fish and Seafood':
                return faFish;
            case 'Fruits':
                return faAppleAlt;
            case 'Vegetables':
                return faCarrot;
            case 'Dairy Products':
                return faCheese;
            case 'Dry Goods':
                return faShoppingBag;
            case 'Fresh Products':
                return faLeaf;
            case 'Frozen Foods':
                return faSnowflake;
            case 'Grocery':
                return faBoxes;
            case 'Beverages':
                return faWineBottle;
            case 'Snacks':
                return faCandyCane;
            default:
                return null;
        }
    };

    return (
        <div className="ms-body">
            <div className="ms-background"></div>
            <h1 className="ms-title">Shopping Lists</h1>
            <div className="ms-section">
                <div className="ms-list-frame">
                    {shoppingLists.length > 0 && (
                        <>
                            <ul className="ms-list">
                                {shoppingLists[currentIndex].items.map((item, itemIndex) => (
                                    <li key={`${shoppingLists[currentIndex]._id}-${itemIndex}`} className="ms-item">
                                        <div className="ms-item-info">
                                            <div className="ms-item-icon">
                                                {getCategoryIcon(item.category) && (
                                                    <FontAwesomeIcon icon={getCategoryIcon(item.category)} />
                                                )}
                                            </div>
                                            <div className="ms-item-details">
                                                <p className="ms-item-name">{item.name}</p>
                                                <p className="ms-item-quantity">Quantity: {item.quantity}</p>
                                            </div>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <div className="ms-navigation">
                                <button className="custom-btn ms-nav-btn" onClick={handlePrevious}>Previous</button>
                                <button className="custom-btn ms-nav-btn" onClick={handleNext}>Next</button>
                            </div>
                            <div className="frame">
                                <button className="custom-btn ms-remove-btn" onClick={() => handleDeleteList(shoppingLists[currentIndex]._id)}>Delete</button>
                            </div>
                        </>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ManageShoppingFoodList;
