import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDrumstickBite, faFish, faAppleAlt, faCarrot, faCheese, faShoppingBag, faLeaf, faSnowflake, faBoxes, faWineBottle, faCandyCane } from '@fortawesome/free-solid-svg-icons';
import "./ManageShoppingFoodList.css";

const ManageShoppingFoodList = () => {
    const [shoppingLists, setShoppingLists] = useState([]);
    const [activeListId, setActiveListId] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false); // Nouvel état pour contrôler l'ouverture de la popup

    useEffect(() => {
        fetchShoppingLists();
    }, []);

    const fetchShoppingLists = async () => {
        try {
            const response = await axios.get('http://localhost:5000/shopping-list');
            const lists = response.data.map(list => ({ ...list, showAll: false }));
            setShoppingLists(lists);
        } catch (error) {
            console.error('Error fetching shopping lists:', error);
        }
    };

    const handleDeleteList = async (listId) => {
        try {
            await axios.delete(`http://localhost:5000/shopping-list/${listId}`);
            fetchShoppingLists();
        } catch (error) {
            console.error('Error deleting shopping list:', error);
        }
    };

    const handleShowMore = (listId) => {
        setShoppingLists(shoppingLists.map(list => {
            if (list._id === listId) {
                return { ...list, showAll: !list.showAll };
            }
            return list;
        }));
    };

    const handleTogglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    };

    const renderItems = (list) => {
        if (list.showAll) {
            return list.items.map(item => (
                <li key={`${list._id}-${item.name}`} className="ms-item">
                    <div className="ms-item-info">
                        <div className="ms-item-icon">
                            {getCategoryIcon(item.category) && <FontAwesomeIcon icon={getCategoryIcon(item.category)} />}
                        </div>
                        <div className="ms-item-details">
                            <p className="ms-item-name">{item.name}</p>
                            <p className="ms-item-quantity">Quantity: {item.quantity}</p>
                        </div>
                    </div>
                </li>
            ));
        } else {
            return list.items.slice(0, 4).map(item => (
                <li key={`${list._id}-${item.name}`} className="ms-item">
                    <div className="ms-item-info">
                        <div className="ms-item-icon">
                            {getCategoryIcon(item.category) && <FontAwesomeIcon icon={getCategoryIcon(item.category)} />}
                        </div>
                        <div className="ms-item-details">
                            <p className="ms-item-name">{item.name}</p>
                            <p className="ms-item-quantity">Quantity: {item.quantity}</p>
                        </div>
                    </div>
                </li>
            ));
        }
    };

    const getCategoryIcon = (category) => {
        // Logique pour obtenir l'icône correspondante
    };

    return (
        <div className="ms-body">
            <div className="ms-background"></div>
            <h1 className="ms-title">Shopping Lists</h1>
            <div className="ms-section">
                <ul className="ms-lists">
                    {shoppingLists.map((list, index) => (
                        <li key={list._id} className="ms-list">
                            <h3 className="ms-list-title">{list.listTitle}</h3>
                            <ul className={`ms-items ms-items-${index + 1}`}>
                                {renderItems(list)}
                            </ul>
                            {list.items.length > 4 && (
                                <button className="ms-show-more-btn" onClick={() => handleShowMore(list._id)}>
                                    {list.showAll ? 'Hide' : 'Show more'}
                                </button>
                            )}
                            <button className="ms-remove-btn" onClick={() => handleDeleteList(list._id)}>Delete</button>
                            <button className="ms-popup-toggle-btn" onClick={() => setActiveListId(list._id)}>Open Popup</button> // Bouton pour ouvrir la popup
                        </li>
                    ))}
                </ul>
            </div>
            {shoppingLists.map((list, index) => (
                <div key={list._id}>
                    {activeListId === list._id && isPopupOpen && ( // Afficher la popup si activeListId correspond et isPopupOpen est vrai
                        <div className="ms-popup">
                            <h3 className="ms-popup-title">{list.listTitle}</h3>
                            <ul>
                                {list.items.map(item => (
                                    <li key={`${list._id}-${item.name}`} className="ms-item">
                                        <div className="ms-item-info">
                                            <div className="ms-item-icon">
                                                {getCategoryIcon(item.category) && <FontAwesomeIcon icon={getCategoryIcon(item.category)} />}
                                            </div>
                                            <div className="ms-item-details">
                                                <p className="ms-item-name">{item.name}</p>
                                                <p className="ms-item-quantity">Quantity: {item.quantity}</p>
                                            </div>
                                        </div>
                                    </li>
                                ))}
                            </ul>
                            <button className="ms-close-btn" onClick={() => setActiveListId(null)}>Close</button> // Bouton pour fermer la popup
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default ManageShoppingFoodList;
