import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ManageRecipeMealList() {
    const [recipes, setRecipes] = useState([]);

    useEffect(() => {
        // Fonction pour charger les recettes une fois que le composant est monté
        const fetchRecipes = async () => {
            try {
                const response = await axios.get('http://localhost:5000/recipe-list');
                setRecipes(response.data);
            } catch (error) {
                console.error('Erreur lors du chargement des recettes :', error);
            }
        };

        fetchRecipes();
    }, []); // Utilisation d'un tableau vide pour n'exécuter l'effet qu'une seule fois au montage

    return (
        <div>
            <h2>Liste des recettes de cuisine</h2>
            <ul>
                {recipes.map(recipe => (
                    <li key={recipe._id}>
                        <div>
                            <h3>{recipe.title}</h3>
                            <p>Temps de préparation : {recipe.prepTime} minutes</p>
                            <p>Temps de cuisson : {recipe.cookTime} minutes</p>
                            {/* Ajoutez ici les autres détails de la recette que vous souhaitez afficher */}
                            <button>Supprimer</button>
                            <button>Modifier</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ManageRecipeMealList;
