import React, { useState } from 'react';

function ListeRecettesPage() {
    const [recipe, setRecipe] = useState({
        title: '',
        prepTime: '',
        cookTime: '',
        ingredients: [],
        instructions: ''
    });

    const [ingredient, setIngredient] = useState({
        name: '',
        quantity: '',
        unit: ''
    });

    const [isIngredientLocked, setIsIngredientLocked] = useState(false);

    const [quantityOptions] = useState([...Array(8).keys()].map(i => (i + 1) * 25));
    const [unitOptions] = useState(['pièce', 'mL', 'cl', 'L', 'g', 'kg', 'cuillère à café', 'cuillère à soupe']);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecipe(prevRecipe => ({
            ...prevRecipe,
            [name]: value
        }));
    };

    const handleIngredientChange = (e) => {
        const { name, value } = e.target;
        setIngredient(prevIngredient => ({
            ...prevIngredient,
            [name]: value
        }));
    };

    const addIngredient = (e) => {
        e.preventDefault();
        if (!isIngredientLocked) {
            setRecipe(prevRecipe => ({
                ...prevRecipe,
                ingredients: [...prevRecipe.ingredients, ingredient]
            }));
            setIngredient({
                name: '',
                quantity: '',
                unit: ''
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Nouvelle recette:', recipe);
        setRecipe({
            title: '',
            prepTime: '',
            cookTime: '',
            ingredients: [],
            instructions: ''
        });
    };

    const toggleIngredientLock = () => {
        setIsIngredientLocked(prevLock => !prevLock);
    };

    const handleSelectChange = (e) => {
        const { name, value } = e.target;
        setIngredient(prevIngredient => ({
            ...prevIngredient,
            [name]: value
        }));
    };

    const getQuantityOptions = () => {
        if (ingredient.unit === 'pièce') {
            return [...Array(9).keys()].map(i => i + 1);
        } else {
            return [...Array(8).keys()].map(i => (i + 1) * 25);
        }
    };

    return (
        <div>
            <h2>Créer une nouvelle recette</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="title">Titre de la recette:</label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={recipe.title}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="prepTime">Temps de préparation (en minutes):</label>
                    <input
                        type="number"
                        id="prepTime"
                        name="prepTime"
                        value={recipe.prepTime}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="cookTime">Temps de cuisson (en minutes):</label>
                    <input
                        type="number"
                        id="cookTime"
                        name="cookTime"
                        value={recipe.cookTime}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Liste des ingrédients:</label>
                    <ul>
                        {recipe.ingredients.map((ingredient, index) => (
                            <li key={index}>{`${ingredient.name}: ${ingredient.quantity} ${ingredient.unit}`}</li>
                        ))}
                    </ul>
                    <div>
                        <input
                            type="text"
                            placeholder="Nom de l'ingrédient"
                            name="name"
                            value={ingredient.name}
                            onChange={handleIngredientChange}
                            required
                        />
                        <select
                            name="quantity"
                            value={ingredient.quantity}
                            onChange={handleSelectChange}
                            required
                            disabled={isIngredientLocked}
                        >
                            <option value="">Sélectionner une quantité</option>
                            {getQuantityOptions().map(option => (
                                <option key={option} value={option}>{option}</option>
                            ))}
                            <option value="custom">Autre</option>
                        </select>
                        {ingredient.quantity === 'custom' && (
                            <input
                                type="number"
                                placeholder="Entrez la quantité"
                                name="quantity"
                                value={ingredient.customQuantity}
                                onChange={handleSelectChange}
                                min="0"
                            />
                        )}
                        <select
                            name="unit"
                            value={ingredient.unit}
                            onChange={handleIngredientChange}
                            required
                            disabled={isIngredientLocked}
                        >
                            <option value="">Sélectionner une unité</option>
                            {unitOptions.map(option => (
                                <option key={option} value={option}>{option}</option>
                            ))}
                        </select>
                        <button onClick={addIngredient} disabled={isIngredientLocked}>Ajouter l'ingrédient</button>
                    </div>
                    <div>
                        <input
                            type="checkbox"
                            id="lockIngredients"
                            checked={isIngredientLocked}
                            onChange={toggleIngredientLock}
                        />
                        <label htmlFor="lockIngredients">Verrouiller les ingrédients</label>
                    </div>
                </div>
                <div>
                    <label htmlFor="instructions">Instructions:</label>
                    <textarea
                        id="instructions"
                        name="instructions"
                        value={recipe.instructions}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">Ajouter et sauvegarder la recette</button>
            </form>
        </div>
    );
}

export default ListeRecettesPage;
